#import "NSJSONSerialization+XLJSONSerialization.h"

@implementation NSJSONSerialization (XLJSONSerialization)

+(NSArray *)postsData
{
    
   NSString * jsonStr = @"[{\"post\":{\"id\":113,\"text\":\"We're getting fifty percent of the t-shirt sales\",\"created_at\":\"2014-04-17T00:45:40.556Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":66,\"text\":\"Eep!\",\"created_at\":\"2014-04-09T21:29:59.982Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":42,\"text\":\"I'm not thinking straight, why did I have that wine cooler last month?\",\"created_at\":\"2014-04-09T17:58:41.704Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":84,\"text\":\"Son, when you participate in sporting events, it's not whether you win or lose: it's how drunk you get.\",\"created_at\":\"2014-04-03T20:21:32.119Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":75,\"text\":\"I'm normally not a praying man, but if you're up there, please save me Superman.\",\"created_at\":\"2014-04-03T02:04:43.053Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":26,\"text\":\"Homer, please get rid of that pig.\",\"created_at\":\"2014-04-02T03:48:56.381Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":40,\"text\":\"You sold weapon-grade plutoneum to the Iraqies without a mark up.\",\"created_at\":\"2014-03-28T05:23:24.657Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":28,\"text\":\"Homer, don't say that. The way I see it, if you raised three children who can knock out and hog tie a perfect stranger you must be doing something right.\",\"created_at\":\"2014-03-22T14:24:22.408Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":48,\"text\":\"Hi-dilly-ho, neighborinos!\",\"created_at\":\"2014-03-21T08:39:20.764Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":78,\"text\":\"Maybe, just once, someone will call me 'Sir' without adding, 'You're making a scene.'\",\"created_at\":\"2014-03-20T02:44:28.075Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":33,\"text\":\"This is the type of trickery I pay you for.\",\"created_at\":\"2014-03-18T08:25:14.507Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":91,\"text\":\"I'm having the best day of my life, and I owe it all to not going to Church!\",\"created_at\":\"2014-03-17T20:38:48.268Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":100,\"text\":\"Television! Teacher, mother, secret lover.\",\"created_at\":\"2014-03-12T21:41:14.356Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":97,\"text\":\"I want to share something with you: The three little sentences that will get you through life. Number 1: Cover for me.\",\"created_at\":\"2014-03-06T09:59:57.328Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":102,\"text\":\"I've always wondered if there was a god. And now I know there is -- and it's me.\",\"created_at\":\"2014-03-05T11:08:05.371Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":72,\"text\":\"Oh, so they have internet on computers now!\",\"created_at\":\"2014-03-03T19:02:56.032Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":1,\"text\":\"You know, I do! I mean, there comes a time in a man's life when he asks himself, 'who will float my corpse down the Ganges?'\",\"created_at\":\"2014-02-24T14:09:00.912Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":62,\"text\":\"Ay Caramba!\",\"created_at\":\"2014-02-18T16:38:37.958Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":19,\"text\":\"Throughout the ages, the finger painter, the Play-Doh sculptor, the Lincoln Logger, stood alone against the daycare teacher of her time. She did not live to earn aproval stickers, she lived for herself, that she might achieve things that are the glory of all humanity. These are my terms. I do not care to play by any others. And now, if the jury will allow me, it's naptime.\",\"created_at\":\"2014-02-16T22:11:33.236Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":76,\"text\":\"Son, if you really want something in this life, you have to work for it. Now quiet! They're about to announce the lottery numbers.\",\"created_at\":\"2014-02-16T19:09:55.062Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":22,\"text\":\"Somebody throw the goddamn bomb!\",\"created_at\":\"2014-02-16T13:50:25.313Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":36,\"text\":\"Oh, so mother nature needs a favor? Well, maybe she should have thought of that when she was besetting us with droughts and floods and poison monkeys.\",\"created_at\":\"2014-02-13T06:51:57.549Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":69,\"text\":\"Oww! Quit it!\",\"created_at\":\"2014-02-13T01:01:52.005Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":94,\"text\":\"Getting out of jury duty is easy. The trick is to say you're prejudiced against all races.\",\"created_at\":\"2014-02-12T11:42:39.305Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":54,\"text\":\"Alcohol increases your capability to drive...\",\"created_at\":\"2014-02-12T08:02:58.837Z\",\"user\":{\"id\":6,\"name\":\"Otto Mann\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Otto_Mann.png\"}}},{\"post\":{\"id\":3,\"text\":\"Owhh, they're a ravenous sworm of locusts just eating and screaming and grabbing and poking and pulling and drooling, and two have cradle rash. How do you get cradle rash when you sleep in a suitcase?\",\"created_at\":\"2014-02-01T20:33:44.961Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":89,\"text\":\"Kids, you tried your best and you failed miserably. The lesson is, never try.\",\"created_at\":\"2014-01-29T04:10:18.235Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":53,\"text\":\"So I can staple my license to Homer Simpson's big, bald head!\",\"created_at\":\"2014-01-29T00:58:50.829Z\",\"user\":{\"id\":6,\"name\":\"Otto Mann\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Otto_Mann.png\"}}},{\"post\":{\"id\":67,\"text\":\"Awwww...\",\"created_at\":\"2014-01-23T17:19:36.988Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":6,\"text\":\"I have come to make amends, sir. At first, I blamed you for squealing, but then I realized, it was I who wronged you. So I have come to work off my debt. I am at your service.\",\"created_at\":\"2014-01-20T03:57:36.998Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":106,\"text\":\"Here's a good job at the fireworks factory.\",\"created_at\":\"2014-01-16T02:54:14.467Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":13,\"text\":\"suck\",\"created_at\":\"2014-01-14T12:17:34.122Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":17,\"text\":\"Sequel?\",\"created_at\":\"2014-01-14T10:14:47.220Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":71,\"text\":\"Operator! Give me the number for 911!\",\"created_at\":\"2014-01-11T17:26:58.024Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":50,\"text\":\"Hey Homie, I can see your doodle.\",\"created_at\":\"2014-01-04T20:09:44.796Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":30,\"text\":\"You know, the courts may not be working any more, but as long as everyone is videotaping everyone else, justice will be done.\",\"created_at\":\"2014-01-04T19:56:21.442Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":68,\"text\":\"Get bent!\",\"created_at\":\"2013-12-30T16:19:33.998Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":47,\"text\":\"Well, it's Bart Simpson ... come on in! You're just in time for 'Sponge Bath the Old Folks' Day!\",\"created_at\":\"2013-12-28T16:09:21.741Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":90,\"text\":\"When I look at the smiles on all the children's faces, I just know they're about to jab me with something.\",\"created_at\":\"2013-12-25T06:42:37.261Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":98,\"text\":\"Number 2: Oh, good idea, Boss! Number 3: It was like that when I got here.\",\"created_at\":\"2013-12-25T01:45:51.337Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":16,\"text\":\"Mom\",\"created_at\":\"2013-12-15T22:35:06.209Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":111,\"text\":\"I think it's supposed to symbolize her descent into madness.\",\"created_at\":\"2013-12-14T16:31:05.521Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":25,\"text\":\"I hate being late!\",\"created_at\":\"2013-12-13T21:54:47.370Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":14,\"text\":\"It's your fault I can't talk!\",\"created_at\":\"2013-12-11T04:00:34.162Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":88,\"text\":\"Marriage is like a coffin and each kid is another nail.\",\"created_at\":\"2013-12-07T20:51:47.223Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":110,\"text\":\"All the years I've wanted to be treated like an adult have blown up in my face.\",\"created_at\":\"2013-12-06T20:49:59.496Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":23,\"text\":\"Homer, it was you! You single-handedly killed the entire town!\",\"created_at\":\"2013-12-03T21:23:47.331Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":35,\"text\":\"It's ironic, that this anonymous clan of slack-jawed troglodytes has cost me the election, and yet if I were to have them killed, I would be the one to go to jail! That's democracy for you.\",\"created_at\":\"2013-11-26T20:44:19.540Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":4,\"text\":\"Whether igloo, hut, or geodesic dome there's no structure that I've been to that I'd rather call my home. When I first arrived you were all such jerks but now I've come to love your quirks. Maggie with her eyes so bright, Marge with hair by Frank Lloyd Wright, Lisa can phyliosophize, Bart's adept at spinning lies, Homer's a delightful fellow, sorry about the samonella\",\"created_at\":\"2013-11-20T20:41:12.985Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":81,\"text\":\"You know, boys, a nuclear reactor is a lot like a woman. You just have to read the manual and press the right buttons.\",\"created_at\":\"2013-11-20T18:59:35.096Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":24,\"text\":\"Lately, what's keeping us together is my ability to overlook everything you do. And I overlook these things because...\",\"created_at\":\"2013-11-20T07:21:05.343Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":5,\"text\":\"Who needs the Kwik-E-Mart, now here's the tricky part, oh won't you rhyme with me? Who needs the Kwwik-E-Mart?\",\"created_at\":\"2013-11-17T04:37:16.991Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":108,\"text\":\"It chose to destroy itself rather than lie with us. You can't help but feel a little rejected.\",\"created_at\":\"2013-11-16T08:07:29.480Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":7,\"text\":\"So it is you, Mr. Homer. I must admit, I was expecting it. A woman her age married to someone... How old are you? Sixty-five?\",\"created_at\":\"2013-11-11T14:45:07.027Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":29,\"text\":\"Homer, I don't want to leave Springfield. I've dug myself into a happy little rut here and I'm not about to hoist myself out of it.\",\"created_at\":\"2013-11-07T21:52:27.418Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":107,\"text\":\"Prayer. The last refuge of a scoundrel.\",\"created_at\":\"2013-11-07T02:00:00.474Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":27,\"text\":\"What's the point of going to church every Sunday if when someone we love has a genuine religious experience we ignore it? Right, Grampa?\",\"created_at\":\"2013-11-01T18:04:23.389Z\",\"user\":{\"id\":3,\"name\":\"Marge Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Marge_Simpsons.png\"}}},{\"post\":{\"id\":86,\"text\":\"[Meeting Aliens] Please don't eat me! I have a wife and kids. Eat them!\",\"created_at\":\"2013-11-01T12:54:01.154Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":103,\"text\":\"Kill my boss? Do I dare live out the American dream?\",\"created_at\":\"2013-11-01T04:24:30.379Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":2,\"text\":\"By chilling my loins I increase the chances of impregnating my wife.\",\"created_at\":\"2013-10-31T01:12:24.948Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":104,\"text\":\"If something goes wrong at the plant, blame the guy who can't speak English.\",\"created_at\":\"2013-10-15T06:40:26.386Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":11,\"text\":\"Daddy\",\"created_at\":\"2013-10-12T00:31:04.088Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":93,\"text\":\"I'm not a bad guy! I work hard, and I love my kids. So why should I spend half my Sunday hearing about how I'm going to Hell?\",\"created_at\":\"2013-10-05T21:49:34.299Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":51,\"text\":\"Yeah, one: Have you always been a chick? I mean, I don't want to offend you, but, you were born a man, weren't you? You can tell me, I'm open minded.\",\"created_at\":\"2013-10-05T17:50:49.814Z\",\"user\":{\"id\":6,\"name\":\"Otto Mann\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Otto_Mann.png\"}}},{\"post\":{\"id\":83,\"text\":\"When will I learn? The answer to life's problems aren't at the bottom of a bottle, they're on TV!\",\"created_at\":\"2013-10-03T04:45:29.113Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":46,\"text\":\"Say your prayers, Simpson... Because the schools can't force you like they should!\",\"created_at\":\"2013-10-01T18:43:25.733Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":8,\"text\":\"Unless I redeem myself, I will be reincarnated as a sea cucumber. Or worse, a land cucumber!\",\"created_at\":\"2013-09-29T12:31:59.046Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":44,\"text\":\"Sorry to bother you, Reverend Lovejoy, but I'm kind of in a tizzy. My son Todd just told us he didn't want to eat his damn vegetables.\",\"created_at\":\"2013-09-29T10:18:20.720Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":37,\"text\":\"I don't have the strength to take it out on you, Smithers\",\"created_at\":\"2013-09-22T15:55:54.559Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":20,\"text\":\"wow\",\"created_at\":\"2013-09-20T12:43:39.254Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":41,\"text\":\"Okay, mister. You've got yourself a deal. I'll shave off the soup strainer if you give the sailor talk the ol heave-ho. Okay?\",\"created_at\":\"2013-09-17T05:47:20.697Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":59,\"text\":\"Whoa, mama!\",\"created_at\":\"2013-09-16T02:57:31.936Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":18,\"text\":\"Very well, I'll drive!\",\"created_at\":\"2013-09-15T04:11:29.228Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":58,\"text\":\"Eat My Shorts!\",\"created_at\":\"2013-09-14T05:03:24.886Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":63,\"text\":\"I Didn't Do It!\",\"created_at\":\"2013-09-12T20:11:20.964Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":39,\"text\":\"Cheating is the gift man gives himself.\",\"created_at\":\"2013-09-08T23:41:24.596Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":56,\"text\":\"Nuh-uh! You forgot the lightning bolt.\",\"created_at\":\"2013-09-08T07:17:43.858Z\",\"user\":{\"id\":6,\"name\":\"Otto Mann\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Otto_Mann.png\"}}},{\"post\":{\"id\":112,\"text\":\"Happy Valentine's.\",\"created_at\":\"2013-08-31T23:31:48.549Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":109,\"text\":\"Mom, I poured my heart into that centerpiece. Things like that always happen in this family.\",\"created_at\":\"2013-08-31T06:55:16.487Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":95,\"text\":\"It's not easy to juggle a pregnant wife and a troubled child, but somehow I managed to fit in eight hours of TV a day.\",\"created_at\":\"2013-08-30T18:40:31.312Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":9,\"text\":\"And Paul here wrote a song called 'Live and Let Live'.\",\"created_at\":\"2013-08-16T11:10:10.055Z\",\"user\":{\"id\":1,\"name\":\"Apu Nahasapeemapetilon\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Apu_Nahasapeemapetilon.png\"}}},{\"post\":{\"id\":45,\"text\":\"Okaley Dokely!\",\"created_at\":\"2013-08-14T12:13:24.726Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":99,\"text\":\"Oh, people can come up with statistics to prove anything, Kent. 14% of people know that.\",\"created_at\":\"2013-08-11T04:28:25.346Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":87,\"text\":\"What do we need a psychiatrist for? We know our kid is nuts.\",\"created_at\":\"2013-08-10T20:55:42.177Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":96,\"text\":\"Lisa, Vampires are make-believe, like elves, gremlins, and eskimos.\",\"created_at\":\"2013-08-09T22:50:12.319Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":34,\"text\":\"What good is money if you can't use it to strike fear into the hearts of men.\",\"created_at\":\"2013-08-09T05:34:36.531Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":57,\"text\":\"Oh, my God!\",\"created_at\":\"2013-08-04T13:12:09.865Z\",\"user\":{\"id\":6,\"name\":\"Otto Mann\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Otto_Mann.png\"}}},{\"post\":{\"id\":12,\"text\":\"I want what the dog's eating\",\"created_at\":\"2013-08-03T13:40:32.096Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":82,\"text\":\"Lisa, if you don't like your job you don't strike. You just go in every day and do it really half-assed. That's the American way. \",\"created_at\":\"2013-07-25T04:56:06.106Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":49,\"text\":\"I've done everything the Bible says - even the stuff that contradicts the other stuff!\",\"created_at\":\"2013-07-22T21:41:57.772Z\",\"user\":{\"id\":5,\"name\":\"Ned Flanders\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Ned_Flanders.png\"}}},{\"post\":{\"id\":38,\"text\":\"I'll keep it short and sweet. Family. Religion. Friendship. These are the three demons you must slay if you wish to succeed in business.\",\"created_at\":\"2013-07-21T11:14:48.589Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":80,\"text\":\"Donuts. Is there anything they can't do?\",\"created_at\":\"2013-07-21T09:44:22.090Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":105,\"text\":\"I'm never going to be disabled. I'm sick of being so healthy.\",\"created_at\":\"2013-07-16T15:01:24.410Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":64,\"text\":\"I'm Bart Simpson, Who the Hell are You?\",\"created_at\":\"2013-07-15T23:29:21.969Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":114,\"text\":\"It's awful being a kid. No one listens to you.\",\"created_at\":\"2013-07-08T15:31:41.562Z\",\"user\":{\"id\":9,\"name\":\"Lisa Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Lisa_Simpsons.png\"}}},{\"post\":{\"id\":70,\"text\":\"Cowabunga!\",\"created_at\":\"2013-07-07T09:24:34.012Z\",\"user\":{\"id\":7,\"name\":\"Bart Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Bart_Simpsons.png\"}}},{\"post\":{\"id\":15,\"text\":\"(pacifier sucking noise)\",\"created_at\":\"2013-07-03T00:21:55.199Z\",\"user\":{\"id\":2,\"name\":\"Maggie Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Maggie_Simpsons.png\"}}},{\"post\":{\"id\":77,\"text\":\"Well, it's 1 a.m. Better go home and spend some quality time with the kids.\",\"created_at\":\"2013-07-01T17:22:46.068Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}},{\"post\":{\"id\":31,\"text\":\"I'll bide my time. Revenge is a dish best served cold.\",\"created_at\":\"2013-06-26T10:33:31.482Z\",\"user\":{\"id\":4,\"name\":\"Montgomery Burns\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Montgomery_Burns.png\"}}},{\"post\":{\"id\":74,\"text\":\"Just because I don't care doesn't mean I don't understand\",\"created_at\":\"2013-06-23T04:12:57.047Z\",\"user\":{\"id\":8,\"name\":\"Homer Simpsons\",\"imageURL\":\"http://obscure-refuge-3149.herokuapp.com/images/Homer_Simpsons.png\"}}}]";
    
    NSData * jsonData = [jsonStr dataUsingEncoding:NSUTF8StringEncoding];
  
    return [NSJSONSerialization JSONObjectWithData:jsonData options:0 error:nil];
}

@end
